{-# LANGUAGE BangPatterns #-}
module Board
       where

import Data.Array.IArray
import Data.Char
import Data.Word
import Data.List
import qualified Data.Set as S
import Control.Parallel.Strategies

-- Interpreted as:
-- 255 out of bounds
-- 0 empty
-- 1:6 player pegs
type Player = Word8
type Board = (Array Int Player, Array Player [Int])
type Move = (Int,Int,[Int])

type BoardNode = (Word8, Board) -- (Playersturn, Board)
data SearchTree = Branch [SearchTree] | Leaf BoardNode

evaluate :: BoardNode -> Int
evaluate (player,board) = totalDistance player board

children :: BoardNode -> [(BoardNode,Move)]
children (player,board) | won board = []
                        | otherwise = let otherPlayer = oP player
                                      in map (\m -> ((otherPlayer, move board m), m)) $ legalMoves player board
miniMax :: Int -> Bool -> BoardNode -> (Int, Move)
miniMax depth par !b@(player, board)
    | depth == 0 = (evaluate b, undefined)
    | par = let vals = map (\(bn,m) -> let (score, _) =  miniMax (depth-1) False bn
                                       in (negate score, m)) $ children b 
                sorted = maximumBy (\(i,_) (j,_) -> i `compare` j) vals
            in (vals `using` parListChunk 4 rdeepseq) `seq` sorted
    | otherwise = let vals = map (\(bn,m) -> let (score,_) = miniMax (depth-1) False bn
                                             in (negate score, m)) $ children b
                  in maximumBy (\(i,_) (j,_) -> i `compare` j) vals

